import React from 'react'
import { Fragment } from 'react'
import { Grid } from 'semantic-ui-react'

class CategoryCard extends React.Component{

    // handleCategory = () => {
    //     this.props.changeCategory(this.props.article)
    // }

    handleFilter = event => {
        this.props.setFilter(event.target.value)
    }

    render(){
        return(
        <Grid divided='vertically'>
            <Grid.Row columns={2}>
            <Grid.Column >
                <button onClick={this.handleFilter} 
                value="tech"
                name="tech"
                className='category-card-tech'>TECH
                </button>
            </Grid.Column>
            <Grid.Column>
                <button onClick={this.handleFilter} 
                value="travel"
                name="travel"
                className='category-card-travel'>TRAVEL</button>
            </Grid.Column>
            </Grid.Row>    
        </Grid>
        )
    }
}

export default CategoryCard
