import React from 'react'
import {
    Card,
    Button,
    Image
} from 'semantic-ui-react'

class ArticleCard extends React.Component {
    render() {
        const {
            title,
            author,
            overview,
            image
        } = this.props.article
        return ( <
            Card >
            <
            Image src = {
                image
            }
            wrapped ui = {
                false
            }
            /> <
            Card.Content >
            <
            Card.Header > {
                title
            } < /Card.Header> <
            Card.Meta >
            <
            span className = 'author' > {
                author
            } < /span> <
            /Card.Meta> <
            Card.Description > {
                overview
            } <
            /Card.Description> <
            /Card.Content> <
            button class = "ui button" > Add Favorite < /button> <
            /Card>

        )
    }
}

export default ArticleCard
