import React from 'react'
import { Card, Button, Image } from 'semantic-ui-react'

class ArticleCard extends React.Component{

    handleSubmit = (event, user_id, article_id) => {
        event.preventDefault()
        let url="http://localhost:3010/likes"
        fetch(url,{
            method: "POST",
            headers: {
                "Content-Type": "application/json",
                "Accept": "application/json",
                "Authorization": `Bearer ${localStorage.token}`
            },
            body: JSON.stringify({
                user_id: user_id,
                article_id: article_id
            })
        })
        .then(res=>res.json())
        .then(response=>this.props.addLike(response))
    }


    render(){
        const { title, author, overview, image, url } = this.props.article
        return(
            <Card>
                <Image src={image} wrapped ui={false} />
                <Card.Content>
                    <Card.Header href={url}>{title}</Card.Header>
                    <Card.Meta>
                    <span className='author'>{author}</span>
                    </Card.Meta>
                    <Card.Description>
                        {overview}
                    </Card.Description>
            </Card.Content>
            <Button type="submit" className="ui button">Add Favorite</Button>
        </Card>

        )
    }
}

export default ArticleCard
