    return(
      <div>
        <h1 href="/" style={{textAlign:"center", color: "dark grey"}}> THE WANDER TIMES
        </h1>
        <NavContainer currentUser={this.state.currentUser}
                      handleSearch={this.handleSearch}
                      logout={this.logout}
                      />
           <Switch>
           <Route path='/users/:id' 
