                     return <Signup 
                     setCurrentUser={this.setCurrentUser} 
                   {...routerProps}
            />
          }}></Route>
          <Route path='/' render={(routerProps)=>{
            return  <Fragment> <CategoryContainer articles={this.state.articles}
            changeCategory={this.changeCategory}
            categoryFilter={this.categoryFilter} {...routerProps}
         />
        <MainContainer
        articles={this.categoryFilter()}
        filter={this.state.filter}
        addLikes={this.addLikes}
        currentUser={this.state.currentUser}
        selectedArticle={this.selectedArticle}
        deleteLikes={this.deleteLikes}
        likes={this.state.likes}
        />
          </Fragment>
          }}>
          </Route>
        </Switch>
      </div>  
    )
  }
}

export default App;
