import React from 'react'
import { Grid, Segment, Image, Loader } from 'semantic-ui-react'

class LikeCard extends React.Component{
    // state={
    //     user: null
    // }


    render(){
    console.log(this.props.article)
     const { title } = this.props.article
		if(this.props.user){
			return (
				<Grid columns={6} centered>
					<Grid.Column width={6}>
							<p>{title}</p>
					</Grid.Column>
				</Grid>
			)
		} else {
			return <Loader />
		}
    }
}

export default LikeCard
