import React from 'react'
import { Fragment } from 'react'
import { Grid } from 'semantic-ui-react'

class CategoryCard extends React.Component{

    // handleCategory = event => {
    //     this.props.changeCategory(this.props.article)
    // }

    // handleTech = event => {
    //     this.props.handleFilterTech()
    // }
    // handleTravel = event => {
    //     this.props.handleFilterTravel()
    // }

    handleChange = event => {
        if(event.target.value==="tech"){
            this.props.handleFilterTech()
        } else if(event.target.value==="travel"){
            this.props.handleFilterTravel()
        }
    }

    render(){

        return(
            <Grid divided='vertically'>
            <Grid.Row columns={2}>
              <Grid.Column >
              <div onClick={this.handleChange} 
                    value="tech"
                   className='category-card-tech'>TECH</div>
              </Grid.Column>
              <Grid.Column>
              <div onClick={this.handleChange} 
                value="travel"
                name="travel"
              className='category-card-travel'>TRAVEL</div>
              </Grid.Column>
            </Grid.Row>    
            </Grid>
        )
    }
}

export default CategoryCard
