    handleChange = event => {
        if(event.target.value==="tech"){
            this.props.handleFilterTech(event.targe)
        } else if(event.target.value==="travel"){
            this.props.handleFilterTravel()
        }
    }

    render(){
    //     <div onClick={this.handleClick} className="category-button">
    //     <h3>{this.props.article.category}</h3>
    //   </div>

        return(
           <Grid divided='vertically'>
        <Grid.Row columns={2}>
          <Grid.Column >
          <button onClick={this.handleChange} 
               value="tech"
               className='category-card-tech'>TECH</button>
          </Grid.Column>
          <Grid.Column>
          <button onClick={this.handleChange} 
            value="travel"
            name="travel"
          className='category-card-travel'>TRAVEL</button>
          </Grid.Column>
        </Grid.Row>    
        </Grid>
        )
    }
}

export default CategoryCard
