import React from 'react'
import { Fragment } from 'react'
import { Grid } from 'semantic-ui-react'

class CategoryCard extends React.Component{

    // handleCategory = event => {
    //     this.props.changeCategory(this.props.article)
    // }

    handleChange = event => {
        this.props.handleFilterTech()
    }
    render(){

        return(
            <Grid divided='vertically'>
            <Grid.Row columns={2}>
              <Grid.Column >
              <div onClick={this.props.handleFilterTech} 
                   className='category-card-tech'>{this.props.article.category}</div>
              </Grid.Column>
              {/* <Grid.Column>
              <div className='category-card-travel'>TRAVEL</div>
              </Grid.Column> */}
            </Grid.Row>    
            </Grid>
        )
    }
}

export default CategoryCard
