    // }
    render(){

        return(
            <Grid divided='vertically'>
            <Grid.Row columns={2}>
              <Grid.Column >
              <div onClick={this.handleTech} 
                   className='category-card-tech'>TECH</div>
              </Grid.Column>
              <Grid.Column>
              <div onClick={this.handleTravel} className='category-card-travel'>TRAVEL</div>
              </Grid.Column>
            </Grid.Row>    
            </Grid>
        )
    }
}

export default CategoryCard
