    //     let update={...this.state.user, likes: newLikes}
    //     if(window.confirm("Remove Favorite?")){
    //         this.setState({
    //             user: update
    //         })
    //     }
    // }

    render(){
        console.log(this.props.articles)

    const { user } = this.state
		if(user){
			return (
                <Fragment>
				<Grid columns={2} positon="left">
					<Grid.Column width={3}>
                    This is the Profile Page
						<Segment>
							<Image src={user.avatar} fluid />
							<strong>{user.username}</strong><br/>
						</Segment>
					</Grid.Column>
				</Grid>
                            <h3>Favorite Articles</h3>   
                        <div className="article-card-container">
                                        {
                    this.state.user.likes.map(liked => {
                        // const articleObj = this.props.articles.find(article => article.id === liked.article_id)

                        const likedId = this.props.likes.map(likedMapped=>{
                            return  <ArticleCard selectedArticle={this.props.selectedArticle} 
                            deleteLikes={this.props.deleteLikes} 
                            likes={likedMapped.id} 
                            remove={this.remove}
                            article={liked.article}

               />
                        })
                        // debugger

                        })
                    }
                    </div>
                </Fragment>
			)
		} else {
			return <Loader />
		}
    }
}

export default Profile
