import React from 'react';
import './App.css';
import ArticleContainer from './Containers/ArticleContainer'
import CategoryContainer from './Containers/CategoryContainer'
import NavContainer from './Containers/NavContainer'
// import MainContainer from './Containers/MainContainer'

class App extends React.Component{
  state = {
    articles: [],
    loading: true, 
    filter: "",
    selectedCategory: null
  }

  componentDidMount(){
    // let url = "https://newsapi.org/v2/everything?q=technology&from=2019-09-10&sortBy=publishedAt&apiKey=f0a7beb8be6040cfadf7471e6a6676b4"
    let url = "http://localhost:3010/api/v1/articles"
    fetch(url)
    .then(resp=>resp.json())
    .then(article=>{
      this.setState({
        articles: article,
      })
    })
  }

  setFilter = (filterInput) => {
    this.setState({
      selectedCategory: filterInput
    })
  }

  filter = () => {
    switch(this.state.selectedCategory){
      case "tech":
        return this.state.articles.filter(artilce=>article.cat==="tech")
        break;

      case "travel":
        return this.state.articles.filter(article=>article.cat==="travel")
        break;

      default:
        return this.state.articles
    }
  }




  // changeCategory = (filtered) => {
