  //     selectedCategory: filtered
  //   })
  // }



  // handleFilterTech = () => {
  //   console.log("filtering by tech")
  //   let filtered = [...this.state.articles].filter(cat => 
  //     cat.category === "tech")
  //   this.setState({
  //     selectedCategory: filtered
  //   })
  // }

  // handleFilterTech = (selected) => {
  //   console.log("filtering")
  //   this.setState({
  //     articles: this.state.articles.filter(article=>article.category === selected )
  //   })
  // }



  render(){
    console.log(this.state.articles)
    return(
      <div>
        <h1 style={{textAlign:"center", color: "green"}}>

        </h1>
        <NavContainer changeFilter={this.changeFilter}/>
        <CategoryContainer articles={this.state.articles}
                              setFilter={this.setFilter}
                              filter={this.filter}
                           />
        <ArticleContainer 
        changeCategory={this.changeCategory}
        filter={this.filter}
        articles={this.state.articles.filter(article=>{
          return article.title.toLowerCase().includes(this.state.filter.toLowerCase()) ||
                 article.overview.toLowerCase().includes(this.state.filter.toLowerCase())
        })}
        />
      </div>
    )
  }
}

export default App;
