import React from 'react'
import { Grid, Segment, Image, Loader } from 'semantic-ui-react'

class LikeCard extends React.Component{

    render(){
        // console.log(this.props)
        const { title, author, overview, image, url } = this.props.article
        return(
            <Card onClick={this.handleClick}>
                <Image src={image} wrapped ui={false} />
                <Card.Content>
                    <Card.Header href={url}>{title}</Card.Header>
                    <Card.Meta>
                    <span className='author'>{author}</span>
                    </Card.Meta>
                    <Card.Description>
                        {overview}
                    </Card.Description>
            </Card.Content>
            <Form onSubmit={(event)=>this.handleSubmit(event, this.props.currentUser.id, this.props.article.id)}>
            <Button type="submit" className="ui button">Add Favorite</Button>
            </Form>
        </Card>

        )
    }
}

export default LikeCard
